const BASE_URL =
  "	http://puntenkangnitipheulanepiakhirnovemberwenya.greenscreative.com/api";

function getAccessToken() {
  return localStorage.getItem("accessToken");
}

function putAccessToken(accessToken) {
  return localStorage.setItem("accessToken", accessToken);
}

async function fetchWithToken(url, options = {}) {
  return fetch(url, {
    ...options,
    headers: {
      ...options.headers,
      Authorization: `Bearer ${getAccessToken()}`,
    },
  });
}
async function login({ username, password }) {
  const response = await fetch(`${BASE_URL}/login`, {
    method: "POST",
    headers: {
      Accept: "application/json",
      Authorization: `Bearer ${getAccessToken()}`,
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ username, password }),
  });

  const responseJson = await response.json();
  console.log(responseJson.meta);
  if (responseJson.meta.status !== "success") {
    alert(responseJson.meta.message);
    return { meta: true, data: null };
  }

  return { meta: false, data: responseJson.data };
}

async function register({ name, email, username, password }) {
  const response = await fetch(`${BASE_URL}/register`, {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      Authorization: `Bearer ${getAccessToken()}`,
    },
    body: JSON.stringify({ name, email, username, password }),
  });

  const responseJson = await response.json();

  if (responseJson.meta.status !== "success") {
    alert(responseJson.meta.message);
    return { meta: true };
  }

  return { meta: false };
}

export { login, register, putAccessToken, fetchWithToken, getAccessToken };
