import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function HomePage() {
  const [posts, setPost] = React.useState([]);
  const [title, setTitle] = React.useState("");
  const [content, setContent] = React.useState("");
  const [image, setImage] = React.useState("");
  const [isEdit, setIsEdit] = React.useState(false);

  const getData = () => {
    axios
      .get(`https://jcc.brandingyou.id/api/post`, {
        headers: {
          Authorization: `Bearer ${getAccessToken()}`,
        },
      })
      .then((res) => {
        setPost(res.data.data);
      });
  };
  function getAccessToken() {
    return localStorage.getItem("accessToken");
  }

  // const onSubmitPostAdd = async () => {
  //   getData();
  //   const formData = new FormData();
  //   formData.append("image", image);
  //   formData.append("title", title);
  //   formData.append("content", content);

  //   if (isEdit === false) {
  //     axios
  //       .post(`https://jcc.brandingyou.id/api/post`, formData)
  //       .then(function (response) {
  //         console.log(response);
  //       })
  //       .catch(function (error) {
  //         console.log(error);
  //       });
  //     setIsEdit(true);
  //   } else {
  //     axios
  //       .post(`https://jcc.brandingyou.id/api/post`, formData)
  //       .then(function (response) {
  //         console.log(response);
  //       })
  //       .catch(function (error) {
  //         console.log(error);
  //       });
  //   }
  //   getData();
  // };

  const addData = async () => {
    getData();
    const formData = new FormData();
    formData.append("image", image);
    formData.append("title", title);
    formData.append("content", content);

    if (isEdit === false) {
      axios
        .post(`https://jcc.brandingyou.id/api/post`, formData, {
          headers: {
            Authorization: `Bearer ${getAccessToken()}`,
          },
        })
        .then(function (response) {
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });
    } else {
      axios
        .post(`https://jcc.brandingyou.id/api/post`, formData, {
          headers: {
            Authorization: `Bearer ${getAccessToken()}`,
          },
        })
        .then(function (response) {
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });
    }

    document.getElementById("input-title").value = "";
    document.getElementById("input-content").value = "";
    getData();
  };

  const handleRemove = (id) => {
    axios.delete(`https://jcc.brandingyou.id/api/post/${id}`, {
      headers: {
        Authorization: `Bearer ${getAccessToken()}`,
      },
    });
    getData();
  };

  const handleEdit = async (id) => {
    axios
      .get(`https://jcc.brandingyou.id/api/post/${id}`, {
        headers: {
          Authorization: `Bearer ${getAccessToken()}`,
        },
      })
      .then((response) => {
        console.log(response.data);
        document.getElementById("input-title").value = response.data.data.title;
        document.getElementById("input-content").value =
          response.data.data.content;
      });
  };
  React.useEffect(() => {
    getData();
  }, []);
  return (
    <div className="container">
      <header className="d-flex justify-content-center py-3">
        <ul className="nav nav-pills">
          <li className="nav-item">
            <a href="/" className="nav-link active" aria-current="page">
              Home
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              Features
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              Pricing
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              FAQs
            </a>
          </li>
          <li className="nav-item">
            <Link to="/login" className="nav-link">
              Login
            </Link>
          </li>
        </ul>
      </header>
      <main>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            addData();
          }}
        >
          <div className="mb-1">
            <label className="form-label">Title</label>
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              id="input-title"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Content</label>
            <textarea
              className="form-control"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              id="input-content"
            />
          </div>
          <div className="mb-3">
            <input
              type="file"
              className="form-control"
              id="image_uploads"
              name="image_uploads"
              accept=".jpg, .jpeg, .png"
              onChange={(e) => setImage(e.target.files[0])}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
        <div className="m-2">
          {posts.map((post) => {
            return (
              <article className="postcard light blue" key={post.id}>
                <a className="postcard__img_link" href="/">
                  <img src={post.image} className="postcard__img" alt="" />
                </a>
                <div className="postcard__text t-dark">
                  <h1 className="postcard__title blue">
                    <Link href="/">{post.title}</Link>
                  </h1>
                  <div className="postcard__subtitle small">
                    <time dateTime="2020-05-25 12:00:00">
                      <i className="fas fa-calendar-alt mr-2"></i>
                      {post.created_at}
                    </time>
                  </div>
                  <div className="postcard__bar"></div>
                  <div className="postcard__preview-txt">{post.content}</div>
                  <ul className="postcard__tagbox d-flex justify-content-between">
                    <li className="tag__item">
                      <i className="fas fa-tag mr-2"></i>
                      {post.author}
                    </li>
                    <div>
                      <li className="tag__item play yellow">
                        <button
                          onClick={() => handleEdit(post.id)}
                          style={{
                            textDecoration: "none",
                            backgroundColor: "transparent",
                            border: "none",
                          }}
                        >
                          <i className="fas fa-play mr-2"></i>Edit
                        </button>
                      </li>
                      <li className="tag__item play yellow">
                        <button
                          onClick={() => handleRemove(post.id)}
                          style={{
                            textDecoration: "none",
                            backgroundColor: "transparent",
                            border: "none",
                          }}
                        >
                          <i className="fas fa-play mr-2"></i>Delete
                        </button>
                      </li>
                    </div>
                  </ul>
                </div>
              </article>
            );
          })}
        </div>
      </main>
    </div>
  );
}

export default HomePage;
