import React from "react";
import { Route, Routes } from "react-router-dom";
import HomePage from "../pages/HomePage";
// import Role from "../components/Role";
import LoginPage from "../pages/LoginPage";
import RegisterPage from "../pages/RegisterPage";
import { fetchWithToken, putAccessToken } from "../utils/api";

// const Login = React.lazy(() => import("./Login"));
// const Register = React.lazy(() => import("./Register"));

function Auth() {
  // const navigate = useNavigate();
  const [authedUser, setAuthedUser] = React.useState(null);
  const [initializing, setInitializing] = React.useState(true);

  React.useEffect(() => {
    async function getUserLogin() {
      const { data } = await getUserLogged();

      setAuthedUser(data);
      setInitializing(false);
    }

    getUserLogin();
  });

  async function getUserLogged() {
    const response = await fetchWithToken(
      `	http://puntenkangnitipheulanepiakhirnovemberwenya.greenscreative.com/api/login`
    );
    const responseJson = await response.json();

    if (responseJson.meta.status !== "success") {
      console.log(responseJson.meta);
      return { meta: true, data: null };
    }

    return { meta: false, data: responseJson.data };
  }

  const loginSuccess = async ({ accessToken }) => {
    putAccessToken(accessToken);
    const { data } = await getUserLogged();
    setAuthedUser(data);
  };

  // const onLogout = () => {
  //   setAuthedUser(null);
  //   putAccessToken("");
  //   navigate("/");
  // };

  if (initializing) {
    return null;
  }
  if (authedUser === null) {
    return (
      <main>
        <Routes>
          <Route
            path="/*"
            element={<LoginPage loginSuccess={loginSuccess} />}
          />
          <Route path="/register" element={<RegisterPage />} />
        </Routes>
      </main>
    );
  }
  return (
    <div>
      <HomePage />
    </div>
  );
}

export default Auth;
