import React from "react";
import { Link } from "react-router-dom";

function LoginPage({ loginSuccess }) {
  const [username, setUserName] = React.useState("");
  const [password, setPassword] = React.useState("");

  const onEmailChangeHandler = (e) => {
    setUserName(e.target.value);
  };
  const onPasswordChangeHandler = (e) => {
    setPassword(e.target.value);
  };

  async function login({ username, password }) {
    const response = await fetch(
      `http://puntenkangnitipheulanepiakhirnovemberwenya.greenscreative.com/api/login`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      }
    );

    const responseJson = await response.json();
    console.log(responseJson.meta);

    if (responseJson.meta.status !== "success") {
      alert(responseJson.meta.message);
      return { meta: true, data: null };
    }

    return { meta: false, data: responseJson.data };
  }
  async function onLogin(event) {
    event.preventDefault();
    console.log(loginSuccess);
    const { meta, data } = await login({ username, password });
    console.log(meta);

    if (!meta) {
      loginSuccess(data);
    }
  }
  return (
    <>
      <div className="container col-xl-10 col-xxl-8 px-4 py-5">
        <div className="row align-items-center g-lg-5 py-5">
          <div className="col-lg-7 text-center text-lg-start">
            <h1 className="display-4 fw-bold lh-1 mb-3">
              Vertically centered hero sign-up form
            </h1>
            <p className="col-lg-10 fs-4">
              Below is an example form built entirely with Bootstrapâ€™s form
              controls. Each required form group has a validation state that can
              be triggered by attempting to submit the form without completing
              it.
            </p>
          </div>
          <div className="col-md-10 mx-auto col-lg-5">
            <form
              onSubmit={onLogin}
              className="p-4 p-md-5 border rounded-3 bg-light"
            >
              <div className="form-floating mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="floatingInput"
                  value={username}
                  onChange={onEmailChangeHandler}
                  placeholder="username"
                />
                <label>Username</label>
              </div>
              <div className="form-floating mb-3">
                <input
                  type="password"
                  className="form-control"
                  value={password}
                  onChange={onPasswordChangeHandler}
                  id="floatingPassword"
                  placeholder="Password"
                />
                <label>Password</label>
              </div>
              <button className="w-100 btn btn-lg btn-primary" type="submit">
                Sign up
              </button>
              <small className="text-muted">
                By clicking <Link to="/register">Sign up</Link>, you agree to
                the terms of use.
              </small>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default LoginPage;
