import React from "react";
import { Link } from "react-router-dom";
import PostInput from "../components/PostInput";
import { getPost } from "../utils/api";

function HomePage() {
  const [posts, setPost] = React.useState([]);
  const [title, setTitle] = React.useState(null);
  const [body, setBody] = React.useState(null);

  React.useEffect(() => {
    async function setPostget() {
      const { meta, data } = await getPost();

      if (!meta) {
        setPost(data);
      }

      setTitle(title);
      setBody(body);
    }

    setPostget();
    // setLoading(false);

    return () => {
      setTitle(null);
      setBody(null);
    };
  }, [title, body]);

  return (
    <div className="container">
      <header className="d-flex justify-content-center py-3">
        <ul className="nav nav-pills">
          <li className="nav-item">
            <a href="/" className="nav-link active" aria-current="page">
              Home
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              Features
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              Pricing
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              FAQs
            </a>
          </li>
          <li className="nav-item">
            <Link to="/login" className="nav-link">
              Login
            </Link>
          </li>
        </ul>
      </header>
      <main>
        <PostInput />
        <div className="d-flex">
          {posts.map((post) => {
            return (
              <div className="col-6">
                <article class="postcard light blue">
                  <a class="postcard__img_link" href="/">
                    <img src={post.image} class="postcard__img" alt="" />
                  </a>
                  <div class="postcard__text t-dark">
                    <h1 class="postcard__title blue">
                      <a href="/">{post.title}</a>
                    </h1>
                    <div class="postcard__subtitle small">
                      <time datetime="2020-05-25 12:00:00">
                        <i class="fas fa-calendar-alt mr-2"></i>Mon, May 25th
                        2020
                      </time>
                    </div>
                    <div class="postcard__bar"></div>
                    <div class="postcard__preview-txt">{post.content}</div>
                    <ul class="postcard__tagbox">
                      <li class="tag__item">
                        <i class="fas fa-tag mr-2"></i>Podcast
                      </li>
                      <li class="tag__item">
                        <i class="fas fa-clock mr-2"></i>55 mins.
                      </li>
                      <li class="tag__item play blue">
                        <a href="/">
                          <i class="fas fa-play mr-2"></i>Play Episode
                        </a>
                      </li>
                    </ul>
                  </div>
                </article>
              </div>
            );
          })}
        </div>
      </main>
    </div>
  );
}

export default HomePage;
