// import axios from "axios";
import React, { useState } from "react";

function PostInput() {
  const [title, setTitle] = React.useState("");
  const [content, setContent] = React.useState("");
  const [image, setImage] = useState();

  const onSubmitPostAdd = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("myFile", image, image.name);
    formData.append("title", title);
    formData.append("content", content);

    console.log(image);

    addPost(formData);
  };

  // async function fetchWithToken(url, options = {}) {
  //   return fetch(url, {
  //     ...options,
  //     headers: {
  //       ...options.headers,
  //       Authorization: `Bearer ${getAccessToken()}`,
  //     },
  //   });
  // }
  function getAccessToken() {
    return localStorage.getItem("accessToken");
  }
  async function addPost(form) {
    const response = await fetch(`https://jcc.brandingyou.id/api/post`, {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data",
        Accept: "application/json",
        Authorization: `Bearer ${getAccessToken()}`,
      },
      body: form,
    });

    const responseJson = await response.json();
    console.log(responseJson);

    if (responseJson.meta.status !== "success") {
      return {
        meta: true,
        data: null,
      };
    }

    return {
      meta: false,
      data: responseJson.data,
    };
  }
  return (
    <div>
      <form onSubmit={onSubmitPostAdd}>
        <div className="mb-3">
          <label className="form-label">Title</label>
          <input
            type="text"
            className="form-control"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            id="input-title"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Content</label>
          <textarea
            className="form-control"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            id="input-content"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Image</label>
          <input
            type="file"
            id="image_uploads"
            name="image_uploads"
            accept=".jpg, .jpeg, .png"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}

export default PostInput;
