import React from "react";
import { Link, useNavigate } from "react-router-dom";
import PostInput from "../components/PostInput";
import { getPost } from "../utils/api";

function HomePage() {
  const [posts, setPost] = React.useState([]);
  const [title, setTitle] = React.useState(null);
  const [body, setBody] = React.useState(null);

  async function fetchWithToken(url, options = {}) {
    return fetch(url, {
      ...options,
      headers: {
        ...options.headers,
        Authorization: `Bearer ${getAccessToken()}`,
      },
    });
  }
  const navigate = useNavigate();
  function getAccessToken() {
    return localStorage.getItem("accessToken");
  }
  async function addPost({ title, content, image }) {
    const response = await fetchWithToken(
      `http://jcc.brandingyou.id/api/post`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({
          title,
          content,
          image,
        }),
      }
    );

    const responseJson = await response.json();

    if (responseJson.meta.status !== "success") {
      return {
        meta: true,
        data: null,
      };
    }

    return {
      meta: false,
      data: responseJson.data,
    };
  }

  function onAddNoteHandler(post) {
    addPost(post);
    console.log(addPost(post));
    navigate("/");
  }

  React.useEffect(() => {
    async function setPostget() {
      const { meta, data } = await getPost();

      if (!meta) {
        setPost(data);
      }

      setTitle(title);
      setBody(body);
    }

    setPostget();
    // setLoading(false);

    return () => {
      setTitle(null);
      setBody(null);
    };
  }, [title, body]);

  return (
    <div className="container">
      <header className="d-flex justify-content-center py-3">
        <ul className="nav nav-pills">
          <li className="nav-item">
            <a href="/" className="nav-link active" aria-current="page">
              Home
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              Features
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              Pricing
            </a>
          </li>
          <li className="nav-item">
            <a href="/" className="nav-link">
              FAQs
            </a>
          </li>
          <li className="nav-item">
            <Link to="/login" className="nav-link">
              Login
            </Link>
          </li>
        </ul>
      </header>
      <main>
        <PostInput addPost={onAddNoteHandler} />
        <div className="d-flex flex-wrap">
          {posts.map((post) => {
            return (
              <div key={post.id}>
                <div className="card m-2" style={{ width: "18rem" }}>
                  <img src={post.image} className="card-img-top" alt="..." />
                  <div className="card-body">
                    <h1 className="card-text">{post.title}</h1>
                    <p className="card-text">{post.content}</p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </main>
    </div>
  );
}

export default HomePage;
